
public class RandomPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int randInt = (int) (10 * Math.random() + 1);
		double randDouble = (9 * Math.random() + 1);
		
		System.out.println("random int from 1 to 10 " + randInt);
		randInt = (int) (10 * Math.random() + 1);
		System.out.println("random int from 1 to 10 " + randInt);
		randInt = (int) (10 * Math.random() + 1);
		System.out.println("random int from 1 to 10 " + randInt);
		randInt = (int) (10 * Math.random() + 1);
		System.out.println("random int from 1 to 10 " + randInt);
		randInt = (int) (10 * Math.random() + 1);
		System.out.println("random int from 1 to 10 " + randInt);
		randInt = (int) (10 * Math.random() + 1);
		
		System.out.println("random double from one to less than 10 " + randDouble);
		randDouble = (9 * Math.random() + 1); 
		System.out.println("random double from one to less than 10 " + randDouble);
		randDouble = (9 * Math.random() + 1); 
		System.out.println("random double from one to less than 10 " + randDouble);
		randDouble = (9 * Math.random() + 1); 
		System.out.println("random double from one to less than 10 " + randDouble);
		randDouble = (9 * Math.random() + 1); 
		System.out.println("random double from one to less than 10 " + randDouble);
		randDouble = (9 * Math.random() + 1); 
	}

}
